  --shadow-sm: 0px 2px 0px 0px hsl(220 15% 5% / 0.25), 0px 1px 2px -1px hsl(220 15% 5% / 0.30);
  --shadow: 0px 2px 0px 0px hsl(220 15% 5% / 0.25), 0px 1px 2px -1px hsl(220 15% 5% / 0.30);
  --shadow-md: 0px 2px 0px 0px hsl(220 15% 5% / 0.25), 0px 2px 4px -1px hsl(220 15% 5% / 0.35);
  --shadow-lg: 0px 2px 0px 0px hsl(220 15% 5% / 0.25), 0px 4px 6px -1px hsl(220 15% 5% / 0.40);
