      </div>

      {/* Login Form */}
      <div className="flex-1 flex items-center justify-center p-8">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <div className="p-3 rounded-full bg-primary/10">
                <Wrench className="h-8 w-8 text-primary" />
              </div>
            </div>
            <CardTitle className="text-2xl">
              {isLogin ? "Fazer Login" : "Criar Conta"}
            </CardTitle>
            <p className="text-muted-foreground">
              {isLogin 
                ? "Entre na sua conta para continuar" 
                : "Crie sua conta para começar"
              }
            </p>
          </CardHeader>

          <CardContent>
            <Tabs value={userType} onValueChange={(value) => setUserType(value as "client" | "provider")}>
              <TabsList className="grid w-full grid-cols-2 mb-6">
                <TabsTrigger value="client" className="flex items-center space-x-2" data-testid="client-tab">
                  <User className="h-4 w-4" />
                  <span>Cliente</span>
                </TabsTrigger>
                <TabsTrigger value="provider" className="flex items-center space-x-2" data-testid="provider-tab">
                  <Wrench className="h-4 w-4" />
                  <span>Prestador</span>
                </TabsTrigger>
              </TabsList>

              <TabsContent value="client" className="space-y-4">
                <p className="text-sm text-muted-foreground text-center">
                  Solicite serviços de guincho e mecânico
                </p>
              </TabsContent>

              <TabsContent value="provider" className="space-y-4">
                <p className="text-sm text-muted-foreground text-center">
                  Ofereça seus serviços para clientes próximos
                </p>
              </TabsContent>
            </Tabs>

            <form onSubmit={handleSubmit} className="space-y-4">
              {!isLogin && (
                <div>
                  <Label htmlFor="name">Nome Completo</Label>
                  <Input
                    id="name"
                    type="text"
                    value={formData.name}
                    onChange={(e) => handleInputChange("name", e.target.value)}
                    placeholder="Seu nome completo"
                    required={!isLogin}
                    data-testid="input-name"
                  />
                </div>
              )}

              <div>
                <Label htmlFor="email">E-mail</Label>
                <Input
                  id="email"
                  type="email"
                  value={formData.email}
                  onChange={(e) => handleInputChange("email", e.target.value)}
                  placeholder="seu@email.com"
                  required
                  data-testid="input-email"
                />
              </div>

              <div>
                <Label htmlFor="password">Senha</Label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? "text" : "password"}
                    value={formData.password}
                    onChange={(e) => handleInputChange("password", e.target.value)}
                    placeholder="Sua senha"
                    required
                    data-testid="input-password"
                  />
                  <Button
                    type="button"
                    variant="ghost"
                    size="sm"
                    className="absolute right-0 top-0 h-full px-3 hover:bg-transparent"
                    onClick={() => setShowPassword(!showPassword)}
                    data-testid="toggle-password"
                  >
                    {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  </Button>
                </div>
              </div>

              {!isLogin && (
                <div>
                  <Label htmlFor="confirmPassword">Confirmar Senha</Label>
                  <Input
                    id="confirmPassword"
                    type="password"
                    value={formData.confirmPassword}
                    onChange={(e) => handleInputChange("confirmPassword", e.target.value)}
                    placeholder="Confirme sua senha"
                    required={!isLogin}
                    data-testid="input-confirm-password"
                  />
                </div>
              )}

              <Button type="submit" className="w-full" data-testid="submit-button">
                {isLogin ? "Entrar" : "Criar Conta"}
              </Button>
            </form>

            <div className="mt-6 text-center">
              <button
                type="button"
                onClick={() => setIsLogin(!isLogin)}
                className="text-sm text-primary hover:underline"
                data-testid="toggle-mode"
              >
                {isLogin 
                  ? "Não tem uma conta? Criar conta" 
                  : "Já tem uma conta? Fazer login"
                }
              </button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/MapView.tsx.content:
import { MapPin, Navigation, Phone } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

interface LocationData {
  id: string;
  name: string;
  type: "client" | "provider" | "destination";
  latitude: number;
  longitude: number;
  address: string;
  avatar?: string;
  status?: "online" | "offline" | "in-transit";
  estimatedArrival?: string;
}

interface MapViewProps {
  currentLocation?: LocationData;
  providerLocation?: LocationData;
  destinationLocation?: LocationData;
  serviceId?: string;
  onCallProvider?: () => void;
  onShareLocation?: () => void;
}

export function MapView({
  currentLocation,
  providerLocation,
  destinationLocation,
  serviceId,
  onCallProvider,
  onShareLocation,
}: MapViewProps) {
  // TODO: Integrate with real map service (Google Maps, OpenStreetMap, etc.)

  return (
    <div className="space-y-4">
      {/* Map Container */}
      <Card className="h-96 md:h-[500px]">
        <CardContent className="p-0 h-full relative overflow-hidden rounded-lg">
          {/* Placeholder Map */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-100 to-green-100 dark:from-blue-900/20 dark:to-green-900/20">
            <div className="absolute inset-0 opacity-10">
              <svg viewBox="0 0 400 300" className="w-full h-full">
                {/* Simulated street lines */}
                <line x1="0" y1="100" x2="400" y2="100" stroke="currentColor" strokeWidth="2" />
                <line x1="0" y1="200" x2="400" y2="200" stroke="currentColor" strokeWidth="2" />
                <line x1="100" y1="0" x2="100" y2="300" stroke="currentColor" strokeWidth="2" />
                <line x1="200" y1="0" x2="200" y2="300" stroke="currentColor" strokeWidth="2" />
                <line x1="300" y1="0" x2="300" y2="300" stroke="currentColor" strokeWidth="2" />
              </svg>
            </div>
          </div>

          {/* Current Location Pin */}
          {currentLocation && (
            <div 
              className="absolute z-10 transform -translate-x-1/2 -translate-y-1/2"
              style={{ left: "30%", top: "70%" }}
              data-testid="current-location-pin"
            >
              <div className="relative">
                <div className="w-6 h-6 bg-primary rounded-full border-2 border-white shadow-lg flex items-center justify-center">
                  <div className="w-2 h-2 bg-white rounded-full" />
                </div>
                <div className="absolute top-8 left-1/2 transform -translate-x-1/2 bg-white dark:bg-card border rounded-lg px-2 py-1 shadow-lg whitespace-nowrap">
                  <p className="text-xs font-medium">Você está aqui</p>
                </div>
              </div>
            </div>
          )}

          {/* Provider Location Pin */}
          {providerLocation && (
            <div 
              className="absolute z-10 transform -translate-x-1/2 -translate-y-1/2"
              style={{ left: "50%", top: "40%" }}
              data-testid="provider-location-pin"
            >
              <div className="relative">
                <div className="w-8 h-8 bg-service-available rounded-full border-2 border-white shadow-lg flex items-center justify-center">
                  <Navigation className="w-4 h-4 text-white" />
                </div>
                <div className="absolute top-10 left-1/2 transform -translate-x-1/2 bg-white dark:bg-card border rounded-lg px-2 py-1 shadow-lg">
                  <p className="text-xs font-medium">{providerLocation.name}</p>
                  {providerLocation.estimatedArrival && (
                    <p className="text-xs text-muted-foreground">
                      Chega em {providerLocation.estimatedArrival}
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Destination Pin */}
          {destinationLocation && (
            <div 
              className="absolute z-10 transform -translate-x-1/2 -translate-y-1/2"
              style={{ left: "70%", top: "30%" }}
              data-testid="destination-pin"
            >
              <div className="relative">
                <div className="w-6 h-6 bg-destructive rounded-full border-2 border-white shadow-lg flex items-center justify-center">
                  <MapPin className="w-3 h-3 text-white" />
                </div>
                <div className="absolute top-8 left-1/2 transform -translate-x-1/2 bg-white dark:bg-card border rounded-lg px-2 py-1 shadow-lg whitespace-nowrap">
                  <p className="text-xs font-medium">Destino</p>
                </div>
              </div>
            </div>
          )}

          {/* Route Line (simulated) */}
          {currentLocation && providerLocation && (
            <svg className="absolute inset-0 w-full h-full z-5">
              <line 
                x1="30%" 
                y1="70%" 
                x2="50%" 
                y2="40%" 
                stroke="rgb(59 130 246)" 
                strokeWidth="3" 
                strokeDasharray="5,5"
                className="opacity-70"
              />
            </svg>
          )}

          {/* Map Controls */}
          <div className="absolute top-4 right-4 space-y-2 z-20">
            <Button size="sm" variant="secondary" className="w-10 h-10 p-0">
              +
            </Button>
            <Button size="sm" variant="secondary" className="w-10 h-10 p-0">
              −
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Location Info Panel */}
      {providerLocation && (
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-lg">Prestador a Caminho</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center space-x-3">
              <Avatar className="h-12 w-12">
                <AvatarImage src={providerLocation.avatar} />
                <AvatarFallback>
                  {providerLocation.name.split(" ").map(n => n[0]).join("")}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <h4 className="font-medium">{providerLocation.name}</h4>
                <div className="flex items-center space-x-2 mt-1">
                  <Badge 
                    className={`text-xs ${
                      providerLocation.status === "in-transit" 
                        ? "bg-service-transit" 
                        : "bg-service-available"
                    }`}
                  >
                    {providerLocation.status === "in-transit" ? "Em Trânsito" : "Online"}
                  </Badge>
                  {providerLocation.estimatedArrival && (
                    <span className="text-sm text-muted-foreground">
                      Chega em {providerLocation.estimatedArrival}
                    </span>
                  )}
                </div>
              </div>
              <div className="flex space-x-2">
                {onShareLocation && (
                  <Button 
                    variant="outline" 
                    size="sm"
                    onClick={onShareLocation}
                    data-testid="share-location"
                  >
                    <MapPin className="h-4 w-4 mr-2" />
                    Compartilhar
                  </Button>
                )}
                {onCallProvider && (
                  <Button 
                    size="sm"
                    onClick={onCallProvider}
                    data-testid="call-provider"
                  >
                    <Phone className="h-4 w-4 mr-2" />
                    Ligar
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Current Location Card */}
      {currentLocation && (
        <Card>
          <CardContent className="pt-4">
            <div className="flex items-start space-x-2">
              <MapPin className="h-4 w-4 text-muted-foreground mt-0.5" />
              <div>
                <p className="text-sm font-medium">Sua localização atual</p>
                <p className="text-sm text-muted-foreground">{currentLocation.address}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/Navigation.tsx.content:
import { Home, MessageSquare, MapPin, User, Wrench, Phone } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface NavigationProps {
  userType: "client" | "provider";
  activeTab: string;
  onTabChange: (tab: string) => void;
  unreadMessages?: number;
}

export function Navigation({ userType, activeTab, onTabChange, unreadMessages = 0 }: NavigationProps) {
  const clientTabs = [
    { id: "home", label: "Início", icon: Home },
    { id: "requests", label: "Pedidos", icon: Wrench },
    { id: "map", label: "Mapa", icon: MapPin },
    { id: "chat", label: "Chat", icon: MessageSquare },
    { id: "profile", label: "Perfil", icon: User },
  ];

  const providerTabs = [
    { id: "dashboard", label: "Painel", icon: Home },
    { id: "available", label: "Disponíveis", icon: Wrench },
    { id: "active", label: "Ativo", icon: MapPin },
    { id: "chat", label: "Chat", icon: MessageSquare },
    { id: "profile", label: "Perfil", icon: User },
  ];

  const tabs = userType === "client" ? clientTabs : providerTabs;

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="hidden md:flex items-center justify-between p-4 border-b bg-card">
        <div className="flex items-center space-x-2">
          <Wrench className="h-8 w-8 text-primary" />
          <span className="text-xl font-bold">Pronto Mecânico</span>
        </div>

        <div className="flex items-center space-x-4">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <Button
                key={tab.id}
                variant={activeTab === tab.id ? "default" : "ghost"}
                onClick={() => onTabChange(tab.id)}
                className="relative"
                data-testid={`nav-${tab.id}`}
              >
                <Icon className="h-4 w-4 mr-2" />
                {tab.label}
                {tab.id === "chat" && unreadMessages > 0 && (
                  <Badge className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs bg-service-notification">
                    {unreadMessages}
                  </Badge>
                )}
              </Button>
            );
          })}

          <Button variant="destructive" size="sm" data-testid="emergency-call">
            <Phone className="h-4 w-4 mr-2" />
            Emergência
          </Button>
        </div>
      </nav>

      {/* Mobile Navigation */}
      <nav className="md:hidden fixed bottom-0 left-0 right-0 bg-card border-t z-50">
        <div className="flex items-center justify-around py-2">
          {tabs.map((tab) => {
            const Icon = tab.icon;
            return (
              <button
                key={tab.id}
                onClick={() => onTabChange(tab.id)}
                className={`flex flex-col items-center space-y-1 p-2 rounded-lg transition-colors ${
                  activeTab === tab.id ? "text-primary" : "text-muted-foreground"
                }`}
                data-testid={`mobile-nav-${tab.id}`}
              >
                <div className="relative">
                  <Icon className="h-5 w-5" />
                  {tab.id === "chat" && unreadMessages > 0 && (
                    <Badge className="absolute -top-1 -right-1 h-4 w-4 flex items-center justify-center p-0 text-xs bg-service-notification">
                      {unreadMessages > 9 ? "9+" : unreadMessages}
                    </Badge>
                  )}
                </div>
                <span className="text-xs">{tab.label}</span>
              </button>
            );
          })}
        </div>
      </nav>
    </>
  );
}

client/src/components/ProviderDashboard..content:
import { useState } from "react";
import { MapPin, Clock, Phone, MessageSquare, CheckCircle, XCircle } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";

interface ServiceRequest {
  id: string;
  clientName: string;
  serviceType: "tow" | "mechanic" | "emergency";
  location: string;
  description: string;
  distance: string;
  estimatedPay: string;
  urgency: "low" | "medium" | "high";
  requestTime: string;
}

interface ActiveService {
  id: string;
  clientName: string;
  serviceType: "tow" | "mechanic" | "emergency";
  fromLocation: string;
  toLocation?: string;
  description: string;
  status: "accepted" | "in-transit" | "working";
  startTime: string;
  estimatedCompletion: string;
}

interface ProviderDashboardProps {
  providerName: string;
  isOnline: boolean;
  onToggleStatus: (online: boolean) => void;
  availableRequests: ServiceRequest[];
  activeService?: ActiveService;
  onAcceptRequest: (requestId: string) => void;
  onRejectRequest: (requestId: string) => void;
  onUpdateStatus: (status: string) => void;
  onCompleteService: () => void;
  onChat: () => void;
  onCall: () => void;
}

const urgencyConfig = {
  low: { label: "Baixa", color: "bg-muted-foreground" },
  medium: { label: "Média", color: "bg-service-transit" },
  high: { label: "Alta", color: "bg-service-emergency" },
};

const serviceTypeLabels = {
  tow: "Guincho",
  mechanic: "Mecânico", 
  emergency: "Emergência",
};

export function ProviderDashboard({
  providerName,
  isOnline,
  onToggleStatus,
  availableRequests,
  activeService,
  onAcceptRequest,
  onRejectRequest,
  onUpdateStatus,
  onCompleteService,
  onChat,
  onCall,
}: ProviderDashboardProps) {
  const [selectedTab, setSelectedTab] = useState("available");

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Bem-vindo, {providerName}</CardTitle>
              <p className="text-muted-foreground">
                {isOnline ? "Você está online e disponível" : "Você está offline"}
              </p>
            </div>
            <div className="flex items-center space-x-2">
              <Label htmlFor="status-toggle">Status</Label>
              <Switch
                id="status-toggle"
                checked={isOnline}
                onCheckedChange={onToggleStatus}
                data-testid="status-toggle"
              />
              <Badge className={isOnline ? "bg-service-available" : "bg-muted-foreground"}>
                {isOnline ? "Online" : "Offline"}
              </Badge>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Active Service */}
      {activeService && (
        <Card className="border-service-available">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <CheckCircle className="h-5 w-5 text-service-available" />
              <span>Serviço Ativo</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <p className="font-medium">{activeService.clientName}</p>
                <p className="text-sm text-muted-foreground">
                  {serviceTypeLabels[activeService.serviceType]} • #{activeService.id.slice(-6)}
                </p>
              </div>
              <div className="text-right">
                <Badge className="bg-service-available">
                  {activeService.status === "accepted" && "Aceito"}
                  {activeService.status === "in-transit" && "Em Trânsito"}
                  {activeService.status === "working" && "Trabalhando"}
                </Badge>
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex items-start space-x-2">
                <MapPin className="h-4 w-4 text-muted-foreground mt-0.5" />
                <div>
                  <p className="text-sm font-medium">Origem</p>
                  <p className="text-sm text-muted-foreground">{activeService.fromLocation}</p>
                </div>
              </div>
              {activeService.toLocation && (
                <div className="flex items-start space-x-2">
                  <MapPin className="h-4 w-4 text-primary mt-0.5" />
                  <div>
                    <p className="text-sm font-medium">Destino</p>
                    <p className="text-sm text-muted-foreground">{activeService.toLocation}</p>
                  </div>
                </div>
              )}
            </div>

            <div className="p-3 bg-muted rounded-lg">
              <p className="text-sm">{activeService.description}</p>
            </div>

            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center space-x-1">
                <Clock className="h-3 w-3" />
                <span>Iniciado às {activeService.startTime}</span>
              </div>
              <span className="text-muted-foreground">
                Previsão: {activeService.estimatedCompletion}
              </span>
            </div>

            <div className="flex space-x-2">
              <Button
                variant="outline"
                onClick={onChat}
                className="flex-1"
                data-testid="active-chat"
              >
                <MessageSquare className="h-4 w-4 mr-2" />
                Chat
              </Button>
              <Button
                variant="outline"
                onClick={onCall}
                className="flex-1"
                data-testid="active-call"
              >
                <Phone className="h-4 w-4 mr-2" />
                Ligar
              </Button>
              {activeService.status === "working" && (
                <Button
                  onClick={onCompleteService}
                  className="flex-1"
                  data-testid="complete-service"
                >
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Concluir
                </Button>
              )}
            </div>

            {activeService.status !== "working" && (
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  onClick={() => onUpdateStatus("in-transit")}
                  disabled={activeService.status !== "accepted"}
                  data-testid="start-transit"
                >
                  Iniciar Trânsito
                </Button>
                <Button
                  onClick={() => onUpdateStatus("working")}
                  disabled={activeService.status !== "in-transit"}
                  data-testid="start-working"
                >
                  Iniciar Trabalho
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Available Requests */}
      {!activeService && (
        <Card>
          <CardHeader>
            <CardTitle>Solicitações Disponíveis</CardTitle>
            <p className="text-muted-foreground">
              {availableRequests.length} solicitações próximas a você
            </p>
          </CardHeader>
          <CardContent>
            {availableRequests.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                <p>Nenhuma solicitação disponível no momento.</p>
                <p className="text-sm">Novas solicitações aparecerão aqui automaticamente.</p>
              </div>
            ) : (
              <div className="space-y-4">
                {availableRequests.map((request) => {
                  const urgencyStyle = urgencyConfig[request.urgency];

                  return (
                    <Card key={request.id} className="hover-elevate">
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between mb-3">
                          <div>
                            <h4 className="font-medium">{request.clientName}</h4>
                            <p className="text-sm text-muted-foreground">
                              {serviceTypeLabels[request.serviceType]} • #{request.id.slice(-6)}
                            </p>
                          </div>
                          <div className="flex space-x-2">
                            <Badge className={`${urgencyStyle.color} text-white`}>
                              {urgencyStyle.label}
                            </Badge>
                            {request.serviceType === "emergency" && (
                              <Badge className="bg-service-emergency text-white">
                                Urgente
                              </Badge>
                            )}
                          </div>
                        </div>

                        <div className="space-y-2 mb-3">
                          <div className="flex items-start space-x-2">
                            <MapPin className="h-4 w-4 text-muted-foreground mt-0.5" />
                            <div>
                              <p className="text-sm font-medium">Local</p>
                              <p className="text-sm text-muted-foreground">{request.location}</p>
                            </div>
                          </div>
                        </div>

                        <div className="p-2 bg-muted rounded text-sm mb-3">
                          {request.description}
                        </div>

                        <div className="flex items-center justify-between text-sm text-muted-foreground mb-3">
                          <div className="flex items-center space-x-4">
                            <div className="flex items-center space-x-1">
                              <MapPin className="h-3 w-3" />
                              <span>{request.distance}</span>
                            </div>
                            <div className="flex items-center space-x-1">
                              <Clock className="h-3 w-3" />
                              <span>{request.requestTime}</span>
                            </div>
                          </div>
                          <span className="font-medium text-foreground">
                            {request.estimatedPay}
                          </span>
                        </div>

                        <div className="flex space-x-2">
                          <Button
                            variant="outline"
                            onClick={() => onRejectRequest(request.id)}
                            className="flex-1"
                            data-testid={`reject-${request.id}`}
                          >
                            <XCircle className="h-4 w-4 mr-2" />
                            Recusar
                          </Button>
                          <Button
                            onClick={() => onAcceptRequest(request.id)}
                            className="flex-1"
                            data-testid={`accept-${request.id}`}
                          >
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Aceitar
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

