import { Clock, MapPin, User, Wrench, Phone, MessageSquare } from "lucide-react";
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

export type ServiceStatus = "pending" | "accepted" | "in-transit" | "completed";
export type ServiceType = "tow" | "mechanic" | "emergency";

interface ServiceRequestCardProps {
  id: string;
  serviceType: ServiceType;
  status: ServiceStatus;
  fromAddress: string;
  toAddress?: string;
  description: string;
  requestTime: string;
  estimatedPrice?: string;
  providerName?: string;
  providerAvatar?: string;
  providerRating?: number;
  estimatedArrival?: string;
  distance?: string;
  onAccept?: () => void;
  onReject?: () => void;
  onChat?: () => void;
  onCall?: () => void;
  viewType: "client" | "provider";
}

const statusConfig = {
  pending: { label: "Pendente", color: "bg-muted-foreground" },
  accepted: { label: "Aceito", color: "bg-service-available" },
  "in-transit": { label: "Em Trânsito", color: "bg-service-transit" },
  completed: { label: "Concluído", color: "bg-service-available" },
};

const serviceConfig = {
  tow: { label: "Guincho", icon: Wrench },
  mechanic: { label: "Mecânico", icon: Wrench },
  emergency: { label: "Emergência", icon: Phone },
};

export function ServiceRequestCard({
  id,
  serviceType,
  status,
  fromAddress,
  toAddress,
  description,
  requestTime,
  estimatedPrice,
  providerName,
  providerAvatar,
  providerRating,
  estimatedArrival,
  distance,
  onAccept,
  onReject,
  onChat,
  onCall,
  viewType,
}: ServiceRequestCardProps) {
  const statusStyle = statusConfig[status];
  const serviceStyle = serviceConfig[serviceType];
  const ServiceIcon = serviceStyle.icon;

  return (
    <Card className="w-full hover-elevate" data-testid={`service-card-${id}`}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="p-2 rounded-lg bg-primary/10">
              <ServiceIcon className="h-4 w-4 text-primary" />
            </div>
            <div>
              <h3 className="font-semibold">{serviceStyle.label}</h3>
              <p className="text-sm text-muted-foreground">#{id.slice(-6)}</p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <Badge className={`${statusStyle.color} text-white`}>
              {statusStyle.label}
            </Badge>
            {serviceType === "emergency" && (
              <Badge className="bg-service-emergency text-white">
                Urgente
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        <div className="space-y-2">
          <div className="flex items-start space-x-2">
            <MapPin className="h-4 w-4 text-muted-foreground mt-0.5" />
            <div className="flex-1">
              <p className="text-sm font-medium">Origem</p>
              <p className="text-sm text-muted-foreground">{fromAddress}</p>
            </div>
          </div>
          {toAddress && (
            <div className="flex items-start space-x-2">
              <MapPin className="h-4 w-4 text-primary mt-0.5" />
              <div className="flex-1">
                <p className="text-sm font-medium">Destino</p>
                <p className="text-sm text-muted-foreground">{toAddress}</p>
              </div>
            </div>
          )}
        </div>

        <div className="p-3 bg-muted rounded-lg">
          <p className="text-sm">{description}</p>
        </div>

        <div className="flex items-center justify-between text-sm text-muted-foreground">
          <div className="flex items-center space-x-1">
            <Clock className="h-3 w-3" />
            <span>{requestTime}</span>
          </div>
          {distance && (
            <div className="flex items-center space-x-1">
              <MapPin className="h-3 w-3" />
              <span>{distance}</span>
            </div>
          )}
        </div>

        {providerName && (
          <div className="flex items-center space-x-3 p-3 bg-card rounded-lg border">
            <Avatar className="h-10 w-10">
              <AvatarImage src={providerAvatar} />
              <AvatarFallback>
                {providerName.split(" ").map(n => n[0]).join("")}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <p className="font-medium">{providerName}</p>
              <div className="flex items-center space-x-1">
                <span className="text-sm text-muted-foreground">
                  ⭐ {providerRating}/5.0
                </span>
                {estimatedArrival && (
                  <span className="text-sm text-service-transit">
                    • Chega em {estimatedArrival}
                  </span>
                )}
              </div>
            </div>
          </div>
        )}

        {estimatedPrice && (
          <div className="text-right">
            <p className="text-sm text-muted-foreground">Preço estimado</p>
            <p className="text-lg font-bold text-primary">{estimatedPrice}</p>
          </div>
        )}
      </CardContent>

      <CardFooter className="pt-0">
        {viewType === "provider" && status === "pending" && (
          <div className="flex space-x-2 w-full">
            <Button 
              variant="outline" 
              onClick={onReject}
              className="flex-1"
              data-testid={`reject-${id}`}
            >
              Recusar
            </Button>
            <Button 
              onClick={onAccept}
              className="flex-1"
              data-testid={`accept-${id}`}
            >
              Aceitar
            </Button>
          </div>
        )}

        {(status === "accepted" || status === "in-transit") && (
          <div className="flex space-x-2 w-full">
            <Button 
              variant="outline" 
              onClick={onChat}
              className="flex-1"
              data-testid={`chat-${id}`}
            >
              <MessageSquare className="h-4 w-4 mr-2" />
              Chat
            </Button>
            <Button 
              variant="outline" 
              onClick={onCall}
              className="flex-1"
              data-testid={`call-${id}`}
            >
              <Phone className="h-4 w-4 mr-2" />
              Ligar
            </Button>
          </div>
        )}
      </CardFooter>
    </Card>
  );
}

client/src/components/ServiceRequestForm.content:
import { useState, useRef, useEffect } from "react";
import { MapPin, Wrench, Phone, AlertCircle, Search, Loader2 } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Checkbox } from "@/components/ui/checkbox";
import { useMapsLibrary } from "@vis.gl/react-google-maps";
import { useToast } from "@/hooks/use-toast";

const GOOGLE_MAPS_API_KEY = import.meta.env.VITE_GOOGLE_MAPS_API_KEY || '';

interface ServiceRequestFormProps {
  onSubmit: (requestData: ServiceRequestData) => void;
  onCancel: () => void;
}

export interface ServiceRequestData {
  serviceType: "tow" | "mechanic" | "emergency";
  fromAddress: string;
  toAddress?: string;
  description: string;
  urgency: "normal" | "urgent";
  needsDestination: boolean;
  contactPhone: string;
}

function AddressAutocomplete({ 
  value,
  onAddressChange,
  placeholder = "Digite o endereço..."
}: { 
  value: string;
  onAddressChange: (address: string) => void;
  placeholder?: string;
}) {
  const places = useMapsLibrary('places');
  const inputRef = useRef<HTMLInputElement>(null);
  const autocompleteRef = useRef<google.maps.places.Autocomplete | null>(null);

  // Sync external value changes (like from GPS) to the input
  useEffect(() => {
    if (inputRef.current && value !== undefined && value !== inputRef.current.value) {
      inputRef.current.value = value;
    }
  }, [value]);

  useEffect(() => {
    if (!places || !inputRef.current) return;

    const options = {
      componentRestrictions: { country: 'br' },
      fields: ['formatted_address', 'geometry', 'name', 'address_components'],
      types: ['address'],
    };

    autocompleteRef.current = new places.Autocomplete(inputRef.current, options);

    autocompleteRef.current.addListener('place_changed', () => {
      const place = autocompleteRef.current?.getPlace();
      if (place?.formatted_address) {
        onAddressChange(place.formatted_address);
      }
    });

    // Also sync manual typing to parent state
    const handleInput = (e: Event) => {
      const target = e.target as HTMLInputElement;
      onAddressChange(target.value);
    };

    inputRef.current.addEventListener('input', handleInput);
    const currentInput = inputRef.current;

    return () => {
      if (autocompleteRef.current) {
        google.maps.event.clearInstanceListeners(autocompleteRef.current);
      }
      if (currentInput) {
        currentInput.removeEventListener('input', handleInput);
      }
    };
  }, [places, onAddressChange]);

  return (
    <div className="relative flex-1">
      <input
        ref={inputRef}
        defaultValue={value || ''}
        placeholder={placeholder}
        data-testid="input-address-autocomplete"
        autoComplete="off"
        className="flex h-9 w-full rounded-md border border-input bg-background px-3 py-1 pr-10 text-sm shadow-sm transition-colors placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:cursor-not-allowed disabled:opacity-50"
      />
      <Search className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground pointer-events-none" />
    </div>
  );
}

export function ServiceRequestForm({ onSubmit, onCancel }: ServiceRequestFormProps) {
  const [formData, setFormData] = useState<ServiceRequestData>({
    serviceType: "mechanic",
    fromAddress: "",
    toAddress: "",
    description: "",
    urgency: "normal",
    needsDestination: false,
    contactPhone: "",
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isLoadingLocation, setIsLoadingLocation] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    console.log("Submitting service request:", formData);

    try {
      // TODO: Remove mock functionality
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call
      onSubmit(formData);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleInputChange = (field: keyof ServiceRequestData, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const reverseGeocode = async (lat: number, lng: number) => {
    if (!GOOGLE_MAPS_API_KEY) return;

    try {
      const response = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=${GOOGLE_MAPS_API_KEY}`
      );
      const data = await response.json();

      if (data.results && data.results.length > 0) {
        handleInputChange("fromAddress", data.results[0].formatted_address);
      }
    } catch (error) {
      console.error('Erro ao obter endereço:', error);
    }
  };

  const getCurrentLocation = () => {
    if (!navigator.geolocation) {
      toast({
        title: "GPS não disponível",
        description: "Seu navegador não suporta geolocalização",
        variant: "destructive",
      });
      return;
    }

    setIsLoadingLocation(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const location = {
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        };

        await reverseGeocode(location.lat, location.lng);

        toast({
          title: "Localização obtida",
          description: "Sua localização foi identificada com sucesso",
        });

        setIsLoadingLocation(false);
      },
      (error) => {
        console.error('Geolocation error:', error);
        setIsLoadingLocation(false);

        let errorMessage = "Não foi possível obter sua localização";
        if (error.code === 1) {
          errorMessage = "Permissão de localização negada. Digite o endereço manualmente.";
        } else if (error.code === 2) {
          errorMessage = "Localização indisponível. Digite o endereço manualmente.";
        } else if (error.code === 3) {
          errorMessage = "Tempo esgotado. Digite o endereço manualmente.";
        }

        toast({
          title: "Erro de localização",
          description: errorMessage,
          variant: "destructive",
        });
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 0
      }
    );
  };

  const serviceTypes = [
    {
      id: "mechanic",
      label: "Mecânico",
      description: "Reparo no local",
      icon: Wrench,
    },
    {
      id: "tow", 
      label: "Guincho",
      description: "Remoção do veículo",
      icon: Wrench,
    },
    {
      id: "emergency",
      label: "Emergência",
      description: "Atendimento urgente",
      icon: Phone,
    },
  ];

  return (
    <Card className="w-full max-w-2xl">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <Wrench className="h-6 w-6 text-primary" />
          <span>Solicitar Serviço</span>
        </CardTitle>
        <p className="text-muted-foreground">
          Preencha os dados para solicitar um serviço
        </p>
      </CardHeader>

      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Service Type */}
          <div>
            <Label className="text-base font-medium">Tipo de Serviço</Label>
            <RadioGroup
              value={formData.serviceType}
              onValueChange={(value) => handleInputChange("serviceType", value)}
              className="mt-2"
            >
              {serviceTypes.map((service) => {
                const Icon = service.icon;
                return (
                  <div 
                    key={service.id}
                    className="flex items-center space-x-3 rounded-lg border p-4 hover-elevate"
                    data-testid={`service-type-${service.id}`}
                  >
                    <RadioGroupItem value={service.id} id={service.id} />
                    <div className="flex items-center space-x-3 flex-1">
                      <div className="p-2 rounded-lg bg-primary/10">
                        <Icon className="h-4 w-4 text-primary" />
                      </div>
                      <div>
                        <Label htmlFor={service.id} className="font-medium cursor-pointer">
                          {service.label}
                        </Label>
                        <p className="text-sm text-muted-foreground">
                          {service.description}
                        </p>
                      </div>
                    </div>
                  </div>
                );
              })}
            </RadioGroup>
          </div>

          {/* From Address */}
          <div>
            <Label htmlFor="fromAddress">Local Atual</Label>
            <div className="flex space-x-2 mt-1">
              <AddressAutocomplete
                value={formData.fromAddress}
                onAddressChange={(address) => handleInputChange("fromAddress", address)}
                placeholder="Digite o endereço ou use GPS"
              />
              <Button
                type="button"
                variant="outline"
                onClick={getCurrentLocation}
                disabled={isLoadingLocation}
                data-testid="button-get-location"
              >
                {isLoadingLocation ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <MapPin className="h-4 w-4" />
                )}
              </Button>
            </div>
          </div>

          {/* Destination Address */}
          <div>
            <div className="flex items-center space-x-2 mb-2">
              <Checkbox
                id="needsDestination"
                checked={formData.needsDestination}
                onCheckedChange={(checked) => handleInputChange("needsDestination", checked)}
                data-testid="needs-destination"
              />
              <Label htmlFor="needsDestination">Preciso de transporte para outro local</Label>
            </div>

            {formData.needsDestination && (
              <AddressAutocomplete
                value={formData.toAddress || ""}
                onAddressChange={(address) => handleInputChange("toAddress", address)}
                placeholder="Digite o endereço de destino"
              />
            )}
          </div>

          {/* Description */}
          <div>
            <Label htmlFor="description">Descrição do Problema</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => handleInputChange("description", e.target.value)}
              placeholder="Descreva detalhadamente o problema com seu veículo..."
              className="mt-1"
              rows={4}
              required
              data-testid="input-description"
            />
          </div>

          {/* Contact Phone */}
          <div>
            <Label htmlFor="contactPhone">Telefone de Contato</Label>
            <Input
              id="contactPhone"
              type="tel"
              value={formData.contactPhone}
              onChange={(e) => handleInputChange("contactPhone", e.target.value)}
              placeholder="(11) 99999-9999"
              required
              data-testid="input-phone"
            />
          </div>

          {/* Urgency */}
          <div>
            <Label className="text-base font-medium">Urgência</Label>
            <RadioGroup
              value={formData.urgency}
              onValueChange={(value) => handleInputChange("urgency", value)}
              className="mt-2"
            >
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="normal" id="normal" />
                <Label htmlFor="normal">Normal</Label>
              </div>
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="urgent" id="urgent" />
                <Label htmlFor="urgent" className="flex items-center space-x-2">
                  <span>Urgente</span>
                  <AlertCircle className="h-4 w-4 text-service-emergency" />
                </Label>
              </div>
            </RadioGroup>
          </div>

          {/* Emergency Warning */}
          {formData.serviceType === "emergency" && (
            <div className="p-4 bg-service-emergency/10 border border-service-emergency/20 rounded-lg">
              <div className="flex items-start space-x-2">
                <AlertCircle className="h-5 w-5 text-service-emergency mt-0.5" />
                <div>
                  <p className="font-medium text-service-emergency">Serviço de Emergência</p>
                  <p className="text-sm text-muted-foreground">
                    Este pedido será tratado com prioridade máxima. Você será contactado em até 2 minutos.
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Actions */}
          <div className="flex space-x-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={onCancel}
              className="flex-1"
              data-testid="cancel-request"
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={isSubmitting}
              className="flex-1"
              data-testid="submit-request"
            >
              {isSubmitting ? "Enviando..." : "Solicitar Serviço"}
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

