import { useAuth } from '@/lib/auth-context';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Wrench, Truck, AlertCircle, Clock, CheckCircle, XCircle, Star } from 'lucide-react';
import { useQuery } from '@tanstack/react-query';

export default function HistoryPage() {
  const { token } = useAuth();

  const { data: serviceRequests = [], isLoading } = useQuery<any[]>({
    queryKey: ['/api/service-requests/history'],
    enabled: !!token,
  });

  const getServiceIcon = (type: string) => {
    switch (type) {
      case 'mechanic': return <Wrench className="w-4 h-4" />;
      case 'tow_truck': return <Truck className="w-4 h-4" />;
      default: return <AlertCircle className="w-4 h-4" />;
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return (
          <Badge variant="secondary" className="flex items-center gap-1" data-testid={`badge-status-pending`}>
            <Clock className="w-3 h-3" />
            Pendente
          </Badge>
        );
      case 'accepted':
        return (
          <Badge variant="default" className="flex items-center gap-1" data-testid={`badge-status-accepted`}>
            <CheckCircle className="w-3 h-3" />
            Aceito
          </Badge>
        );
      case 'completed':
        return (
          <Badge className="flex items-center gap-1 bg-green-600" data-testid={`badge-status-completed`}>
            <CheckCircle className="w-3 h-3" />
            Concluído
          </Badge>
        );
      case 'cancelled':
        return (
          <Badge variant="destructive" className="flex items-center gap-1" data-testid={`badge-status-cancelled`}>
            <XCircle className="w-3 h-3" />
            Cancelado
          </Badge>
        );
      default:
        return <Badge variant="secondary">{status}</Badge>;
    }
  };

  const getServiceTypeLabel = (type: string) => {
    switch (type) {
      case 'mechanic': return 'Mecânico';
      case 'tow_truck': return 'Guincho';
      case 'road_assistance': return 'Assistência na Estrada';
      default: return 'Outro';
    }
  };

  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <p className="text-muted-foreground">Carregando...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4 space-y-4">
      <h1 className="text-2xl font-bold" data-testid="text-title">Histórico de Chamadas</h1>

      {serviceRequests.length === 0 ? (
        <Card>
          <CardContent className="p-6 text-center">
            <p className="text-muted-foreground" data-testid="text-no-history">
              Você ainda não tem chamadas no histórico
            </p>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-3">
          {serviceRequests.map((request: any) => (
            <Card key={request.id} data-testid={`card-history-${request.id}`}>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <div className="flex items-center gap-2">
                  {getServiceIcon(request.serviceType)}
                  <CardTitle className="text-base">
                    {getServiceTypeLabel(request.serviceType)}
                  </CardTitle>
                </div>
                {getStatusBadge(request.status)}
              </CardHeader>
              <CardContent>
                <div className="space-y-2 text-sm">
                  <p className="text-muted-foreground" data-testid={`text-address-${request.id}`}>
                    {request.pickupAddress}
                  </p>

                  {request.description && (
                    <p data-testid={`text-description-${request.id}`}>
                      {request.description}
                    </p>
                  )}

                  {request.rating && (
                    <div className="flex items-center gap-1 text-yellow-500" data-testid={`text-rating-${request.id}`}>
                      <Star className="w-4 h-4 fill-current" />
                      <span className="font-medium">{request.rating}/5</span>
                    </div>
                  )}

                  {request.ratingComment && (
                    <p className="text-sm italic text-muted-foreground" data-testid={`text-comment-${request.id}`}>
                      "{request.ratingComment}"
                    </p>
                  )}

                  <div className="flex justify-between items-center pt-2">
                    <span className="text-muted-foreground" data-testid={`text-date-${request.id}`}>
                      {formatDate(request.createdAt)}
                    </span>

                    {request.totalPrice && (
                      <span className="font-semibold" data-testid={`text-price-${request.id}`}>
                        R$ {parseFloat(request.totalPrice).toFixed(2)}
                      </span>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
}

client/src/pages/home.tsx.content:
import { useState, useEffect } from 'react';
import { useAuth } from '@/lib/auth-context';
import { useLocation } from 'wouter';
import { APIProvider } from '@vis.gl/react-google-maps';
import { useToast } from '@/hooks/use-toast';
import { fetchWithAuth } from '@/lib/fetch-with-auth';
import {
  AddressStep,
  ServiceTypeStep,
  PaymentStep,
  type AddressData,
  type ServiceData,
  type PaymentData,
} from '@/components/client-flow-steps';
import { MechanicHome } from '@/components/mechanic-home';


const GOOGLE_MAPS_API_KEY = import.meta.env.VITE_GOOGLE_MAPS_API_KEY || '';

type Step = 'address' | 'service' | 'payment';

function ClientHome() {
  const { token } = useAuth();
  const [, setLocationPath] = useLocation();
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState<Step>('address');
  const [addressData, setAddressData] = useState<AddressData | null>(null);
  const [serviceData, setServiceData] = useState<ServiceData | null>(null);
  const [isCreatingRequest, setIsCreatingRequest] = useState(false);

  const handleAddressNext = (data: AddressData) => {
    setAddressData(data);
    setCurrentStep('service');
  };

  const handleServiceNext = (data: ServiceData) => {
    setServiceData(data);
    setCurrentStep('payment');
  };

  const handlePaymentNext = async (paymentData: PaymentData) => {
    if (!addressData || !serviceData) return;

    setIsCreatingRequest(true);

    try {
      const requestBody = {
        pickupLat: addressData.lat.toString(),
        pickupLng: addressData.lng.toString(),
        pickupAddress: addressData.address,
        serviceType: serviceData.type,
        description: serviceData.description || undefined,
        paymentMethod: paymentData.method,
      };

      const response = await fetchWithAuth('/api/service-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Erro ao criar solicitação');
      }

      const serviceRequest = await response.json();

      toast({
        title: 'Solicitação criada!',
        description: paymentData.method === 'pix' 
          ? 'Aguarde enquanto procuramos um mecânico...'
          : 'Procurando mecânicos disponíveis...',
      });

      if (paymentData.method === 'pix') {
        setLocationPath(`/payment/${serviceRequest.id}`);
      } else {
        setLocationPath(`/ride/${serviceRequest.id}`);
      }
    } catch (error: any) {
      console.error('Error creating request:', error);
      toast({
        title: 'Erro',
        description: error.message || 'Não foi possível criar a solicitação',
        variant: 'destructive',
      });
    } finally {
      setIsCreatingRequest(false);
    }
  };

  const handleBackFromService = () => {
    setCurrentStep('address');
  };

  const handleBackFromPayment = () => {
    setCurrentStep('service');
  };

  return (
    <div className="h-full bg-background flex flex-col">
      {currentStep === 'address' && (
        <AddressStep onNext={handleAddressNext} initialAddress={addressData?.address} />
      )}
      {currentStep === 'service' && (
        <ServiceTypeStep onNext={handleServiceNext} onBack={handleBackFromService} />
      )}
      {currentStep === 'payment' && !isCreatingRequest && (
        <PaymentStep onNext={handlePaymentNext} onBack={handleBackFromPayment} />
      )}
      {isCreatingRequest && (
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center space-y-4">
            <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto" />
            <p className="text-lg font-semibold">Criando solicitação...</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default function Home() {
  const { user } = useAuth();

  if (!user) {
    return (
      <div className="flex items-center justify-center h-full">
        <p>Carregando...</p>
      </div>
    );
  }

  if (!GOOGLE_MAPS_API_KEY) {
    return (
      <div className="flex items-center justify-center h-full p-4">
        <p className="text-destructive">
          Chave da API do Google Maps não configurada
        </p>
      </div>
    );
  }

  return (
    <APIProvider apiKey={GOOGLE_MAPS_API_KEY} libraries={['places', 'geocoding']}>
      {user.userType === 'client' ? <ClientHome /> : <MechanicHome />}
    </APIProvider>
  );
}

