import { useState } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Wrench, MapPin, MessageCircle, ChevronRight, ChevronLeft } from 'lucide-react';

const slides = [
  {
    icon: Wrench,
    title: 'Encontre Mecânicos Próximos',
    description: 'Conecte-se instantaneamente com mecânicos profissionais na sua região. Assistência rápida quando você mais precisa.',
    color: 'text-primary',
    bgColor: 'bg-primary/10',
  },
  {
    icon: MapPin,
    title: 'Rastreamento em Tempo Real',
    description: 'Acompanhe a localização do mecânico em tempo real no mapa. Saiba exatamente quando a ajuda vai chegar.',
    color: 'text-blue-500',
    bgColor: 'bg-blue-500/10',
  },
  {
    icon: MessageCircle,
    title: 'Chat e Pagamento Seguro',
    description: 'Comunique-se diretamente com o mecânico e pague de forma segura através do app. Tudo em um só lugar.',
    color: 'text-green-500',
    bgColor: 'bg-green-500/10',
  },
];

export default function OnboardingPage() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [, setLocation] = useLocation();

  const handleNext = () => {
    if (currentSlide < slides.length - 1) {
      setCurrentSlide(currentSlide + 1);
    }
  };

  const handlePrevious = () => {
    if (currentSlide > 0) {
      setCurrentSlide(currentSlide - 1);
    }
  };

  const handleGetStarted = () => {
    localStorage.setItem('onboarding_completed', 'true');
    setLocation('/login');
  };

  const slide = slides[currentSlide];
  const Icon = slide.icon;

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-background via-background to-primary/5">
      <div className="w-full max-w-md">
        <Card className="p-8 md:p-10 space-y-8 shadow-xl">
          {/* Logo/Title */}
          <div className="text-center space-y-2">
            <div className="flex items-center justify-center gap-2 mb-6">
              <Wrench className="w-8 h-8 text-primary" />
              <h1 className="text-3xl font-bold">Pronto Mecânico</h1>
            </div>
          </div>

          {/* Icon */}
          <div className="flex justify-center">
            <div className={`${slide.bgColor} p-8 rounded-full`}>
              <Icon className={`w-24 h-24 ${slide.color}`} strokeWidth={1.5} />
            </div>
          </div>

          {/* Content */}
          <div className="text-center space-y-4 min-h-[140px]">
            <h2 className="text-2xl font-bold" data-testid={`text-slide-title-${currentSlide}`}>
              {slide.title}
            </h2>
            <p className="text-muted-foreground text-lg leading-relaxed" data-testid={`text-slide-description-${currentSlide}`}>
              {slide.description}
            </p>
          </div>

          {/* Progress Dots */}
          <div className="flex justify-center gap-2">
            {slides.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentSlide(index)}
                className={`h-2 rounded-full transition-all ${
                  index === currentSlide 
                    ? 'w-8 bg-primary' 
                    : 'w-2 bg-muted-foreground/30'
                }`}
                data-testid={`dot-${index}`}
              />
            ))}
          </div>

          {/* Navigation Buttons */}
          <div className="flex gap-3">
            {currentSlide > 0 && (
              <Button
                variant="outline"
                onClick={handlePrevious}
                className="flex-1"
                data-testid="button-previous"
              >
                <ChevronLeft className="w-4 h-4 mr-2" />
                Anterior
              </Button>
            )}

            {currentSlide < slides.length - 1 ? (
              <Button
                onClick={handleNext}
                className="flex-1"
                data-testid="button-next"
              >
                Próximo
                <ChevronRight className="w-4 h-4 ml-2" />
              </Button>
            ) : (
              <Button
                onClick={handleGetStarted}
                className="flex-1"
                data-testid="button-get-started"
              >
                Ir para Login
              </Button>
            )}
          </div>

          {/* Skip Button */}
          {currentSlide < slides.length - 1 && (
            <button
              onClick={handleGetStarted}
              className="w-full text-center text-sm text-muted-foreground hover:text-foreground transition-colors"
              data-testid="button-skip"
            >
              Pular introdução
            </button>
          )}
        </Card>

        {/* Desktop additional info */}
        <div className="hidden md:block mt-8 text-center text-sm text-muted-foreground">
          <p>Assistência automotiva profissional a qualquer hora, em qualquer lugar</p>
        </div>
      </div>
    </div>
  );
}

client/src/lib/use-notifications.tsx.content:
import { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { useAuth } from './auth-context';
import { useToast } from '@/hooks/use-toast';
import { ToastAction } from '@/components/ui/toast';
import { useLocation } from 'wouter';

interface UnreadMessages {
  [serviceRequestId: string]: number;
}

interface NotificationsContextType {
  unreadMessages: UnreadMessages;
  markAsRead: (serviceRequestId: string) => void;
  totalUnread: number;
  requestNotificationPermission: () => Promise<void>;
  hasNotificationPermission: boolean;
}

const NotificationsContext = createContext<NotificationsContextType | null>(null);

export function NotificationsProvider({ children }: { children: React.ReactNode }) {
  const { user } = useAuth();
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [unreadMessages, setUnreadMessages] = useState<UnreadMessages>({});
  const [hasNotificationPermission, setHasNotificationPermission] = useState(
    typeof Notification !== 'undefined' && Notification.permission === 'granted'
  );

  const totalUnread = Object.values(unreadMessages).reduce((sum, count) => sum + count, 0);

  const requestNotificationPermission = useCallback(async () => {
    if (typeof Notification === 'undefined') {
      toast({
        title: "Notificações não suportadas",
        description: "Seu navegador não suporta notificações",
        variant: "destructive",
      });
      return;
    }

    if (Notification.permission === 'granted') {
      setHasNotificationPermission(true);
      return;
    }

    if (Notification.permission !== 'denied') {
      const permission = await Notification.requestPermission();
      if (permission === 'granted') {
        setHasNotificationPermission(true);
        toast({
          title: "Notificações ativadas",
          description: "Você receberá notificações de novas mensagens",
        });
      }
    } else {
      toast({
        title: "Permissão negada",
        description: "Ative as notificações nas configurações do navegador",
        variant: "destructive",
      });
    }
  }, [toast]);

  const markAsRead = useCallback((serviceRequestId: string) => {
    setUnreadMessages(prev => {
      const updated = { ...prev };
      delete updated[serviceRequestId];
      return updated;
    });
  }, []);

  const showBrowserNotification = useCallback((title: string, body: string, serviceRequestId: string) => {
    if (typeof Notification !== 'undefined' && Notification.permission === 'granted') {
      const notification = new Notification(title, {
        body,
        icon: '/icon-192x192.png',
        badge: '/icon-192x192.png',
        tag: `chat-${serviceRequestId}`,
        requireInteraction: false,
        silent: false,
      });

      notification.onclick = () => {
        window.focus();
        setLocation(`/ride/${serviceRequestId}/chat`);
        notification.close();
      };
    }
  }, [setLocation]);

  useEffect(() => {
    if (!user) return;

    const handleWebSocketMessage = (event: any) => {
      const data = event.detail;

      if (data.type === 'new_chat_message') {
        const messageData = data.data;
        const serviceRequestId = messageData.serviceRequestId;

        if (messageData.senderId !== user.id) {
          const isOnChatPage = window.location.pathname === `/ride/${serviceRequestId}/chat`;

          if (!isOnChatPage) {
            setUnreadMessages(prev => ({
              ...prev,
              [serviceRequestId]: (prev[serviceRequestId] || 0) + 1
            }));

            toast({
              title: "Nova mensagem",
              description: messageData.message.length > 50 
                ? messageData.message.substring(0, 50) + '...' 
                : messageData.message,
              action: (
                <ToastAction 
                  altText="Ver mensagem" 
                  onClick={() => {
                    setLocation(`/ride/${serviceRequestId}/chat`);
                  }}
                >
                  Ver
                </ToastAction>
              )
            });

            showBrowserNotification(
              "Nova mensagem no chat",
              messageData.message,
              serviceRequestId
            );
          }
        }
      }
    };

    window.addEventListener('websocket-message', handleWebSocketMessage);
    return () => window.removeEventListener('websocket-message', handleWebSocketMessage);
  }, [user, toast, showBrowserNotification]);

  return (
    <NotificationsContext.Provider
      value={{
        unreadMessages,
        markAsRead,
        totalUnread,
        requestNotificationPermission,
        hasNotificationPermission,
      }}
    >
      {children}
    </NotificationsContext.Provider>
  );
}

export function useNotifications() {
  const context = useContext(NotificationsContext);
  if (!context) {
    throw new Error('useNotifications must be used within NotificationsProvider');
  }
  return context;
}

