import { useState, useEffect } from 'react';
import { useAuth } from '@/lib/auth-context';
import { useLocation } from 'wouter';
import { Map, AdvancedMarker } from '@vis.gl/react-google-maps';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { useToast } from '@/hooks/use-toast';
import { Wrench, Truck, AlertCircle } from 'lucide-react';
import { fetchWithAuth } from '@/lib/fetch-with-auth';
import { getBrandLabel } from '@shared/vehicles';

export function MechanicHome() {
  const { user, token, updateUser } = useAuth();
  const [, setLocation] = useLocation();
  const [userLocation, setUserLocation] = useState<{ lat: number; lng: number } | null>(null);
  const [pendingRequests, setPendingRequests] = useState<any[]>([]);
  const { toast } = useToast();

  const isOnline = user?.isOnline || false;

  useEffect(() => {
    const handleWebSocketMessage = (event: any) => {
      const data = event.detail;

      if (data.type === 'mechanic_arrived') {
        toast({
          title: "Mecânico chegou!",
          description: "O mecânico chegou no local",
        });
      }

      if (data.type === 'service_request_cancelled' && data.data) {
        setPendingRequests(prev => prev.filter(req => req.id !== data.data.id));
        toast({
          title: "Chamada cancelada",
          description: "O cliente cancelou a chamada",
          variant: "destructive",
        });
      }

      if (data.type === 'service_request_accepted_by_other' && data.data) {
        setPendingRequests(prev => prev.filter(req => req.id !== data.data.id));
      }
    };

    window.addEventListener('websocket-message', handleWebSocketMessage);
    return () => window.removeEventListener('websocket-message', handleWebSocketMessage);
  }, [user, setLocation, toast]);

  useEffect(() => {
    if (user?.userType === 'mechanic') {
      if (user.baseLat && user.baseLng) {
        setUserLocation({
          lat: parseFloat(user.baseLat),
          lng: parseFloat(user.baseLng),
        });
      }
    }
  }, [user, token]);

  useEffect(() => {
    if (user?.userType === 'mechanic' && token && isOnline) {
      loadPendingRequests();
      const interval = setInterval(loadPendingRequests, 10000);
      return () => clearInterval(interval);
    }
  }, [user, token, isOnline]);

  const loadPendingRequests = async () => {
    try {
      const response = await fetchWithAuth('/api/service-requests/pending');
      if (response.ok) {
        const data = await response.json();
        setPendingRequests(data);
      }
    } catch (error) {
      console.error('Error loading requests:', error);
    }
  };

  const handleAcceptRequest = async (requestId: string) => {
    if (user?.baseLat === undefined || user?.baseLat === null || user?.baseLng === undefined || user?.baseLng === null) {
      toast({
        title: "Erro",
        description: "Configure seu endereço base na página de Perfil antes de aceitar chamadas",
        variant: "destructive",
      });
      return;
    }

    try {
      const request = pendingRequests.find(r => r.id === requestId);
      if (!request) return;

      const distance = calculateDistance(
        parseFloat(user.baseLat),
        parseFloat(user.baseLng),
        parseFloat(request.pickupLat),
        parseFloat(request.pickupLng)
      );

      const response = await fetchWithAuth(`/api/service-requests/${requestId}/accept`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ distance }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Erro ao aceitar chamada');
      }

      const acceptedRequest = await response.json();

      toast({
        title: "Sucesso",
        description: `Chamada aceita! Valor total: R$ ${parseFloat(acceptedRequest.totalPrice).toFixed(2)}`,
      });

      setLocation(`/ride/${requestId}`);
    } catch (error: any) {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  function calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  }

  const handleToggleOnline = async (checked: boolean) => {
    try {
      const response = await fetchWithAuth('/api/auth/toggle-online', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ isOnline: checked }),
      });

      if (!response.ok) {
        throw new Error('Erro ao alterar status');
      }

      updateUser({ isOnline: checked });

      toast({
        title: checked ? "Online" : "Offline",
        description: checked 
          ? "Você está online e pode receber chamadas" 
          : "Você está offline e não receberá chamadas",
      });

      if (!checked) {
        setPendingRequests([]);
      }
    } catch (error: any) {
      toast({
        title: "Erro",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  const getServiceIcon = (type: string) => {
    switch (type) {
      case 'mechanic': return <Wrench className="w-4 h-4" />;
      case 'tow_truck': return <Truck className="w-4 h-4" />;
      default: return <AlertCircle className="w-4 h-4" />;
    }
  };

  return (
    <div className="h-full flex flex-col">
      <div className="bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-800 p-4 shadow-sm">
        <div className="container mx-auto flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className={`w-3 h-3 rounded-full ${isOnline ? 'bg-green-500' : 'bg-gray-400'} ${isOnline ? 'animate-pulse' : ''}`}></div>
            <span className="font-semibold text-gray-900 dark:text-white">
              {isOnline ? 'Online' : 'Offline'}
            </span>
            <span className="text-sm text-gray-500 dark:text-gray-400">
              {isOnline ? '• Recebendo chamadas' : '• Não receberá chamadas'}
            </span>
          </div>
          <div className="flex items-center gap-3">
            <Label htmlFor="online-toggle" className="text-sm font-medium text-gray-700 dark:text-gray-300">
              {isOnline ? 'Ficar Offline' : 'Ficar Online'}
            </Label>
            <Switch
              id="online-toggle"
              checked={isOnline}
              onCheckedChange={handleToggleOnline}
              data-testid="switch-online-status"
            />
          </div>
        </div>
      </div>
      <div className="flex-1 relative">
        <Map
          mapId="service-map"
          defaultZoom={15}
          defaultCenter={userLocation || { lat: -23.5505, lng: -46.6333 }}
          center={userLocation || { lat: -23.5505, lng: -46.6333 }}
          gestureHandling="greedy"
          disableDefaultUI={false}
          zoomControl={true}
          fullscreenControl={false}
          streetViewControl={false}
          mapTypeControl={false}
          mapTypeId="roadmap"
          style={{ width: '100%', height: '100%' }}
          data-testid="map-container"
        >
          {userLocation && (
            <AdvancedMarker 
              position={userLocation}
              title="Seu endereço base"
            >
              <div className="relative">
                <div className="w-12 h-12 bg-black dark:bg-white rounded-full shadow-2xl flex items-center justify-center border-4 border-white dark:border-black">
                  <div className="w-3 h-3 bg-white dark:bg-black rounded-full"></div>
                </div>
                <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 w-0 h-0 border-l-[6px] border-l-transparent border-r-[6px] border-r-transparent border-t-[8px] border-t-black dark:border-t-white"></div>
              </div>
            </AdvancedMarker>
          )}

          {pendingRequests.map((request) => (
            <AdvancedMarker
              key={request.id}
              position={{
                lat: parseFloat(request.pickupLat),
                lng: parseFloat(request.pickupLng),
              }}
              title={`Chamada - ${request.serviceType}`}
            >
              <div className="relative animate-pulse">
                <div className="w-10 h-10 bg-red-500 rounded-full shadow-xl flex items-center justify-center border-2 border-white">
                  <AlertCircle className="w-5 h-5 text-white" />
                </div>
                <div className="absolute inset-0 w-10 h-10 bg-red-500 rounded-full animate-ping opacity-75"></div>
              </div>
            </AdvancedMarker>
          ))}
        </Map>

        {isOnline && pendingRequests.length > 0 && (
          <div className="absolute top-4 right-4 z-10 space-y-2 max-w-sm">
            {pendingRequests.map((request) => (
              <Card key={request.id} data-testid={`card-request-${request.id}`}>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">
                    Nova Chamada
                  </CardTitle>
                  {getServiceIcon(request.serviceType)}
                </CardHeader>
                <CardContent>
                  <p className="text-sm text-muted-foreground mb-2">
                    {request.pickupAddress}
                  </p>
                  {request.vehicleBrand && request.vehicleModel && (
                    <p className="text-sm mb-2">
                      <strong>Veículo:</strong> {getBrandLabel(request.vehicleBrand)} {request.vehicleModel}
                      {request.vehiclePlate && ` • ${request.vehiclePlate}`}
                    </p>
                  )}
                  {request.description && (
                    <p className="text-sm mb-2">{request.description}</p>
                  )}
                  {userLocation && (
                    <p className="text-xs text-muted-foreground mb-2">
                      Distância: ~{calculateDistance(
                        userLocation.lat,
                        userLocation.lng,
                        parseFloat(request.pickupLat),
                        parseFloat(request.pickupLng)
                      ).toFixed(1)} km
                    </p>
                  )}
                  <Button
                    onClick={() => handleAcceptRequest(request.id)}
                    className="w-full"
                    size="sm"
                    data-testid={`button-accept-${request.id}`}
                  >
                    Aceitar Chamada
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

server/database-storage.ts.content:
import { eq, and, or, sql as drizzleSql, desc, asc } from "drizzle-orm";
import { db } from "./db";
import { 
  users,
  serviceRequests,
  chatMessages,
  transactions,
  type User, 
  type InsertUser,
  type ServiceRequest,
  type InsertServiceRequest,
  type ChatMessage,
  type InsertChatMessage,
  type Transaction,
  type BankData,
  type BaseAddress
} from "@shared/schema";
import type { IStorage } from "./storage";

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async getUserByCpfCnpj(cpfCnpj: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.cpfCnpj, cpfCnpj));
    return user || undefined;
  }

  async getUserByIdentifier(identifier: string): Promise<User | undefined> {
    const cleanIdentifier = identifier.replace(/\D/g, '');

    if (cleanIdentifier.length === 11 || cleanIdentifier.length === 14) {
      return this.getUserByCpfCnpj(cleanIdentifier);
    }

    return this.getUserByEmail(identifier.toLowerCase());
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values({
        ...insertUser,
        baseAddress: insertUser.baseAddress || null,
        baseLat: insertUser.baseLat !== undefined && insertUser.baseLat !== null
          ? (typeof insertUser.baseLat === 'number' ? insertUser.baseLat.toString() : insertUser.baseLat)
          : null,
        baseLng: insertUser.baseLng !== undefined && insertUser.baseLng !== null
          ? (typeof insertUser.baseLng === 'number' ? insertUser.baseLng.toString() : insertUser.baseLng)
          : null,
      })
      .returning();
    return user;
  }

  async updateUserLocation(userId: string, lat: number, lng: number): Promise<void> {
    await db
      .update(users)
      .set({ 
        currentLat: lat.toString(), 
        currentLng: lng.toString() 
      })
      .where(eq(users.id, userId));
  }

  async updateUserOnlineStatus(userId: string, isOnline: boolean): Promise<void> {
    await db
      .update(users)
      .set({ isOnline })
      .where(eq(users.id, userId));
  }

  async updateUserBaseAddress(userId: string, addressData: BaseAddress): Promise<void> {
    await db
      .update(users)
      .set({
        baseAddress: addressData.baseAddress,
        baseLat: addressData.baseLat.toString(),
        baseLng: addressData.baseLng.toString(),
      })
      .where(eq(users.id, userId));
  }

  async updateUserServiceCategories(userId: string, categories: string[]): Promise<void> {
    await db
      .update(users)
      .set({
        serviceCategories: categories,
      })
      .where(eq(users.id, userId));
  }

  async updateUserBankData(userId: string, bankData: BankData): Promise<void> {
    await db
      .update(users)
      .set({
        bankAccountName: bankData.bankAccountName,
        bankAccountNumber: bankData.bankAccountNumber,
        bankName: bankData.bankName,
        bankBranch: bankData.bankBranch || null,
        pixKey: bankData.pixKey || null,
        pixKeyType: bankData.pixKeyType || null,
      })
      .where(eq(users.id, userId));
  }

  async updateUserRating(userId: string, newRating: number): Promise<void> {
    const [user] = await db.select().from(users).where(eq(users.id, userId));
    if (user) {
      const currentRating = parseFloat(user.rating || '0');
      const totalRatings = user.totalRatings || 0;
      const updatedAverage = ((currentRating * totalRatings) + newRating) / (totalRatings + 1);

      await db
        .update(users)
        .set({
          rating: updatedAverage.toFixed(2),
          totalRatings: totalRatings + 1,
        })
        .where(eq(users.id, userId));
    }
  }

  async updateWalletBalance(userId: string, amount: number): Promise<void> {
    const [user] = await db.select().from(users).where(eq(users.id, userId));
    if (user) {
      const currentBalance = parseFloat(user.walletBalance || "0");
      await db
        .update(users)
        .set({
          walletBalance: (currentBalance + amount).toFixed(2),
        })
        .where(eq(users.id, userId));
    }
  }

  async getOnlineMechanics(): Promise<User[]> {
    return await db
      .select()
      .from(users)
      .where(
        and(
          eq(users.userType, "mechanic"),
          eq(users.isOnline, true)
        )
      );
  }

  async getOnlineMechanicsNearby(lat: number, lng: number, radiusKm: number): Promise<User[]> {
    const mechanics = await this.getOnlineMechanics();
    return mechanics.filter(mechanic => {
      if (!mechanic.currentLat || !mechanic.currentLng) return false;

      const mechanicLat = parseFloat(mechanic.currentLat);
      const mechanicLng = parseFloat(mechanic.currentLng);
      const distance = this.calculateDistance(lat, lng, mechanicLat, mechanicLng);

      return distance <= radiusKm;
    });
  }

  private calculateDistance(lat1: number, lon1: number, lat2: number, lon2: number): number {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  }

  async createServiceRequest(insertRequest: InsertServiceRequest): Promise<ServiceRequest> {
    const [request] = await db
      .insert(serviceRequests)
      .values({
        ...insertRequest,
        description: insertRequest.description ?? null,
      })
      .returning();
    return request;
  }

  async getServiceRequest(id: string): Promise<ServiceRequest | undefined> {
    const [request] = await db
      .select()
      .from(serviceRequests)
      .where(eq(serviceRequests.id, id));
    return request || undefined;
  }

  async getUserServiceRequests(userId: string): Promise<ServiceRequest[]> {
    return await db
      .select()
      .from(serviceRequests)
      .where(
        or(
          eq(serviceRequests.clientId, userId),
          eq(serviceRequests.mechanicId, userId)
        )
      )
      .orderBy(desc(serviceRequests.createdAt));
  }

  async getPendingServiceRequests(): Promise<ServiceRequest[]> {
    return await db
      .select()
      .from(serviceRequests)
      .where(eq(serviceRequests.status, "pending"))
      .orderBy(asc(serviceRequests.createdAt));
  }

  async getActiveServiceRequest(userId: string): Promise<ServiceRequest | undefined> {
    const [request] = await db
      .select()
      .from(serviceRequests)
      .where(
        and(
          or(
            eq(serviceRequests.clientId, userId),
            eq(serviceRequests.mechanicId, userId)
          ),
          or(
            eq(serviceRequests.status, "accepted"),
            eq(serviceRequests.status, "in_progress")
          )
        )
      )
      .orderBy(desc(serviceRequests.createdAt))
      .limit(1);
    return request || undefined;
  }

  async updateServiceRequest(id: string, data: Partial<ServiceRequest>): Promise<ServiceRequest | undefined> {
    const [updated] = await db
      .update(serviceRequests)
      .set(data)
      .where(eq(serviceRequests.id, id))
      .returning();
    return updated || undefined;
  }

  async createChatMessage(insertMessage: InsertChatMessage): Promise<ChatMessage> {
    const [message] = await db
      .insert(chatMessages)
      .values(insertMessage)
      .returning();
    return message;
  }

  async getChatMessages(serviceRequestId: string): Promise<ChatMessage[]> {
    return await db
      .select()
      .from(chatMessages)
      .where(eq(chatMessages.serviceRequestId, serviceRequestId))
      .orderBy(asc(chatMessages.createdAt));
  }

  async createTransaction(
    userId: string, 
    type: string, 
    amount: number, 
    description: string, 
    serviceRequestId?: string,
    availableAt?: Date
  ): Promise<Transaction> {
    const [transaction] = await db
      .insert(transactions)
      .values({
        userId,
        serviceRequestId: serviceRequestId || null,
        type,
        amount: amount.toFixed(2),
        status: "completed",
        description,
        availableAt: availableAt || null,
      })
      .returning();
    return transaction;
  }

  async getUserTransactions(userId: string): Promise<Transaction[]> {
    return await db
      .select()
      .from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(desc(transactions.createdAt));
  }

  async updateTransactionStatus(id: string, status: string, completedAt?: Date): Promise<void> {
    await db
      .update(transactions)
      .set({
        status,
        completedAt: completedAt || null,
      })
      .where(eq(transactions.id, id));
  }

  async getAvailableBalance(userId: string): Promise<number> {
    const now = new Date();
    const userTransactions = await this.getUserTransactions(userId);

    const availableTransactions = userTransactions.filter(t => 
      t.status === "completed" && 
      (t.type === "mechanic_earnings" || t.type === "refund") &&
      (!t.availableAt || t.availableAt <= now)
    );

    const withdrawals = userTransactions.filter(t => 
      t.type === "withdrawal" && 
      (t.status === "completed" || t.status === "pending")
    );

    const totalEarnings = availableTransactions.reduce((sum, t) => sum + parseFloat(t.amount), 0);
    const totalWithdrawals = withdrawals.reduce((sum, t) => sum + Math.abs(parseFloat(t.amount)), 0);

    return totalEarnings - totalWithdrawals;
  }

  async getPendingBalance(userId: string): Promise<number> {
    const now = new Date();
    const userTransactions = await this.getUserTransactions(userId);

    const pendingTransactions = userTransactions.filter(t => 
      t.status === "completed" &&
      (t.type === "mechanic_earnings" || t.type === "refund") &&
      t.availableAt && t.availableAt > now
    );

    return pendingTransactions.reduce((sum, t) => sum + parseFloat(t.amount), 0);
  }

  async createWithdrawalRequest(
    userId: string, 
    amount: number, 
    method: string, 
    details: string,
    pixPaymentId?: string,
    status?: string
  ): Promise<Transaction> {
    const [transaction] = await db
      .insert(transactions)
      .values({
        userId,
        serviceRequestId: null,
        type: "withdrawal",
        amount: (-amount).toFixed(2),
        status: status || (pixPaymentId ? "completed" : "pending"),
        description: `Saque via ${method === 'pix' ? 'PIX' : 'Transferência Bancária'}`,
        availableAt: null,
        withdrawalMethod: method,
        withdrawalDetails: details,
        pixPaymentId: pixPaymentId || null,
        pixType: method === 'pix' ? 'withdrawal' : null,
        completedAt: pixPaymentId && !status ? new Date() : null,
      })
      .returning();
    return transaction;
  }

  async getPendingWithdrawals(): Promise<Array<Transaction & { user: User }>> {
    const withdrawals = await db
      .select()
      .from(transactions)
      .where(
        and(
          eq(transactions.type, "withdrawal"),
          eq(transactions.status, "pending")
        )
      )
      .orderBy(asc(transactions.createdAt));

    const withdrawalsWithUser = await Promise.all(
      withdrawals.map(async (withdrawal) => {
        const user = await this.getUser(withdrawal.userId);
        return { ...withdrawal, user: user! };
      })
    );

    return withdrawalsWithUser;
  }

  async completeWithdrawal(id: string): Promise<void> {
    const [transaction] = await db
      .select()
      .from(transactions)
      .where(eq(transactions.id, id));

    if (!transaction) {
      throw new Error("Transação não encontrada");
    }

    if (transaction.type !== "withdrawal") {
      throw new Error("Esta não é uma transação de saque");
    }

    if (transaction.status !== "pending") {
      throw new Error("Este saque já foi processado");
    }

    await db
      .update(transactions)
      .set({
        status: "completed",
        completedAt: new Date(),
      })
      .where(eq(transactions.id, id));
  }
}

